import React from "react";
import TodoForm from "./TodoForm";
import { nanoid } from "nanoid"

export default function TodoItem(){

    const [todos, setTodos] = React.useState([])
    const [currentTask, setCurrentTask]=React.useState("")
    const [editTaskId, setEditTaskId]=React.useState(null)

    function handleChange(event){
        setCurrentTask(event.target.value );
    }

    function handleSubmit(event){
        event.preventDefault();
        if(currentTask.trim() === "") return;
        if(editTaskId){
            setTodos(prevTodos => {
                const updatedTodos = prevTodos.filter(todo => todo.id !== editTaskId)
                const updatedTasks = {id: editTaskId, body: currentTask}
                return [up]
            }
                )
            setEditTaskId(null)
        } else {
            const newTodo = {
            id: nanoid(),
            body: currentTask
        }
        setTodos( prevTodos => [newTodo,...prevTodos])
    }
        setCurrentTask("")
    }

    function handleEdit(todoId){
        const todoToEdit = todos.find( todo => todo.id === todoId)
        if (todoToEdit){
            setCurrentTask(todoToEdit.body)
            setEditTaskId(todoId)
            
        }
    }
    return (
        <div>
            <form className="todoitem-form" onSubmit={handleSubmit}>
                <input type="text"
                placeholder="Add a task for today..."
                className="todo-input"
                onChange={handleChange}
                >
                </input>
                <button type="submit" className="todo-btn">
                    Add Task
                </button>
            </form>
            < TodoForm todos={todos} onEdit={handleEdit}/>
        </div>
    )

}
