import React, { useState } from 'react';
import { nanoid } from 'nanoid';
import TodoForm from './componentsTodoForm';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function TodoItem() {
  const [todo, setTodo] = useState([{ id: nanoid(), body: "Sample Task", completed: false }]);
  const [newTask, setNewTask] = useState("");

  function handleChange(event) {
    setNewTask(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    if (newTask.trim() === "") return; 
    const newTodo = {
      id: nanoid(),
      body: newTask,
      completed: false
    };
    setTodo(prevTodo => [newTodo, ...prevTodo]);
    setNewTask("");
  }

  function toggleComplete(id) {
    setTodo(prevTodo => 
      prevTodo.map(task => 
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  }

  function deleteTask(id){
    setTodo(prevTodo =>
        prevTodo.filter(task => task.id !== id)
    )
  }
  return (
    <div className="container mt-5">
      <form className="todoitem-form d-flex mb-4" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Add a task for today..."
          className="form-control"
          value={newTask}
          onChange={handleChange}
        />
        <button type="submit" className="btn btn-primary ml-2">
          Add Task
        </button>
      </form>
      <TodoForm todos={todo} toggleComplete={toggleComplete} deleteTask={deleteTask}/>
    </div>
  );
}
