import React from "react";
export default function TodoItem(){

    const [todo, setTodo] = React.useState("")

    function handleChange(){
        setTodo()
    }

    function handleSubmit(event){
        event.preventDefault();
    }

    return (
        <div>
            <form className="todoitem-form">
                <input type="text" placeholder="Add a task for today..." className="todo-input"
                onChange={handleChange}
                >
                </input>
                <button type="submit" className="todo-btn" onSubmit={handleSubmit} >Add Task</button>
            </form>
        </div>
    )

}
