import React from "react";

export default function TodoForm({ todos, onEdit}){
    return(
        <div className="todo-form">
            {todos.map( todo => (
                <div key={todo.id} className="todo-item">
                <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => ontoggle(todo.id)}
                />
                
                
                onClick={() => onEdit(todo.id)}>
                    {todo.body}
                </div>
            ))}

        </div>
    )
}
