import React, { useState } from 'react';
import { nanoid } from 'nanoid';
import TodoForm from './TodoForm';
import 'bootstrap/dist/css/bootstrap.min.css';
import './srApp.css';  // Import custom styles

export default function TodoItem() {
  const [todos, setTodos] = useState([]);
  const [newTask, setNewTask] = useState("");

  function handleChange(event) {
    setNewTask(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();
    if (newTask.trim() === "") {
      console.log("Empty task, not adding.");
      return;  // Check for empty task
    }

    const newTodo = {
      id: nanoid(),
      body: newTask.trim(),
      completed: false
    };
    setTodos(prevTodos => [newTodo, ...prevTodos]);
    setNewTask("");
  }

  function toggleComplete(id) {
    setTodos(prevTodos => 
      prevTodos.map(task => 
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  }

  function deleteTask(id) {
    setTodos(prevTodos =>
      prevTodos.filter(task => task.id !== id)
    );
  }

  function onEdit(id) {
    const editedTask = { id: id, body: newTask.trim(), completed: false };
    setTodos(prevTodos =>
      prevTodos.filter(task => task.id !== id)
    );
    setTodos(prevTodos => [editedTask, ...prevTodos]);
    setNewTask("");
  }

  return (
    <div className="container mt-5">
      <form className="todoitem-form d-flex mb-4" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Add a task for today..."
          className="form-control"
          value={newTask}
          onChange={handleChange}
        />
        <button type="submit" className="btn btn-add-task ms-2">
          Add Task
        </button>
      </form>
      <TodoForm todos={todos} toggleComplete={toggleComplete} deleteTask={deleteTask} onEdit={onEdit}/>
    </div>
  );
}
