import React from "react";
import TodoForm from "./TodoForm";
import { nanoid } from "nanoid";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function TodoItem() {
    const [todos, setTodos] = React.useState([]);
    const [currentTask, setCurrentTask] = React.useState("");
    const [editTaskId, setEditTaskId] = React.useState(null);

    function handleChange(event) {
        setCurrentTask(event.target.value);
    }

    function handleSubmit(event) {
        event.preventDefault();
        if (currentTask.trim() === "") return;
        
        if (editTaskId) {
            // Update the existing task and place it at the beginning
            setTodos(prevTodos => {
                const updatedTodos = prevTodos.filter(todo => todo.id !== editTaskId);
                const updatedTask = { id: editTaskId, body: currentTask, completed: false };
                return [updatedTask, ...updatedTodos];
            });
            setEditTaskId(null);
        } else {
            // Add a new task at the beginning
            const newTodo = {
                id: nanoid(),
                body: currentTask,
                completed: false
            };
            setTodos(prevTodos => [newTodo, ...prevTodos]);
        }
        setCurrentTask("");
    }

    function handleEdit(todoId) {
        const todoToEdit = todos.find(todo => todo.id === todoId);
        if (todoToEdit) {
            setCurrentTask(todoToEdit.body);
            setEditTaskId(todoId);
        }
    }

    function handleToggle(todoId) {
        setTodos(prevTodos =>
            prevTodos.map(todo =>
                todo.id === todoId ? { ...todo, completed: !todo.completed } : todo
            )
        );
    }

    return (
        <div className="container mt-3">
            <form className="mb-3" onSubmit={handleSubmit}>
                <div className="input-group">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Add a task for today..."
                        value={currentTask}
                        onChange={handleChange}
                    />
                    <button type="submit" className="btn btn-primary">
                        {editTaskId ? "Update Task" : "Add Task"}
                    </button>
                </div>
            </form>
            <TodoForm todos={todos} onEdit={handleEdit} onToggle={handleToggle} />
        </div>
    );
}
